const stObj = (() => {
	const result = {}
	for (let i = 0; i < st.length; i += 2) {
		const simple = st[i]
		const traditional = st[i + 1]
		result[`s${simple}`] = traditional
		result[`t${traditional}`] = simple
	}
	return result
})()

function t(text, prefix) {
	let result = ''
	for (let i = 0; i < text.length; i++) {
		const t = text[i]
		const st = stObj[`${prefix}${t}`] || t
		result += st
	}
	return result
}

function toSimple(text) {
	return t(text, 't')
}

function toTraditional(text) {
	return t(text, 's')
}

function checkSameChinese(text1, text2) {
	if (text1.length !== text2.length) {
		return false
	}
	let same = true
	for (let i = 0; i < text1.length; i++) {
		const t1 = text1[i]
		const t2 = text2[i]
		const stt = stObj[`t${t1}`]
		if (stt != null) {
			if (!(stt === t2 || t1 === t2)) {
				same = false
				break
			}
		} else {
			if (t1 !== t2) {
				same = false
				break
			}
		}
	}
	return same
}

function checkIncludeText(text, keyword) {
	return toSimple(text).includes(toSimple(keyword))
}

export { toSimple, toTraditional, checkSameChinese, checkIncludeText }
